mapper.select:
select role_id roleId, role_name roleName, add_time addTime, own_menus ownMenus, add_man addMan from role_info m
role_id = #{roleId}
ORDER BY role_id ASC

mapper.update:
update role_info


		role_name = #{roleName},


		add_time = #{addTime}


		own_menus = #{ownMenus}


		add_man = #{addMan}


where role_id = #{roleId}

update menu


		name = #{name},


		url = #{url}


where id = #{id}
